import styles from './Message.module.css';

function Message({ type, text }) {

    const [visible, setVisible] = useState(false);

    useEffect(() => {

        if(!text) {
            setVisible(false);
            return;
        }

        setVisible(true);

        setTimeout(() => {
            setVisible(false);
        }, 4000);

    }, [text]);

    return(
        <div>
            {visible && (
                <p className={`${styles.message} ${styles[type]}`}>{text}</p>
            )}
        </div>
    );
}

export default Message;
