import { useState } from 'react';

import Message from './Message';

import styles from './ProductForm.module.css';

function ProductForm({ addProduct }) {

    const [messageText, setMessageText] = useState("");
    const [messageType, setMessageType] = useState("");

    const [product, setProduct] = useState({});

    const [inputName, setInputName] = useState("");
    const [inputPrice, setInputPrice] = useState("");
    const [inputDescription, setInputDescription] = useState("");
    const [inputImage, setInputImage] = useState("");

    function checkForm() {

        if(inputName && inputPrice && inputDescription && inputImage) {

            setProduct({name: inputName, price: inputPrice, description: inputDescription, image: inputImage});

            console.log(product);

            fetch("http://localhost:4000/products",
                {
                    method: "POST",
                    headers: {"Content-Type": "application/json",},
                    body: JSON.stringify(product),
                }
            )
            .then((response) => response.json())
            .then((data) => console.log(data))
            .catch((error) => console.log(error))
            
        }else{

            setMessageType("error");
            setMessageText("Failed to add product!");

            setInputName("");
            setInputPrice("");
            setInputDescription("");
            setInputImage("");

            setTimeout(() => {
                setMessageType("");
                setMessageText("");
            }, 4000);

        }

    }

    return(
        <div className={styles.productform}>
            {<Message type={messageType} text={messageText}/>}
            <form onSubmit={event => event.preventDefault()}>
                <h1>Add a new product</h1>
                <p>Fill in all fields</p>
                    <input type="text" id="name" name="name" placeholder="Name" value={inputName} onChange={(event) => setInputName(event.target.value)} required/><br/>
                    <input type="number" id="price" name="price" placeholder="Price" value={inputPrice} onChange={(event) => setInputPrice(event.target.value)} required/><br/>
                    <input type="text" id="description" name="description" placeholder="Description" value={inputDescription} onChange={(event) => setInputDescription(event.target.value)} required/><br/>
                    <input type="text" id="image" name="image" placeholder="Image URL" value={inputImage} onChange={(event) => setInputImage(event.target.value)} required/><br/>
                <input type="submit" value="Add product" onClick={checkForm}/>
            </form>
        </div>
    );
}

export default ProductForm;
