import { useState } from 'react';

import Message from './Message';

import styles from './ContactForm.module.css';

function ContactForm() {

    const [messageText, setMessageText] = useState("");
    const [messageType, setMessageType] = useState("");

    const [inputName, setInputName] = useState("");
    const [inputEmail, setInputEmail] = useState("");
    const [inputSubject, setInputSubject] = useState("");

    function checkForm() {
        
        if(inputName && inputEmail && inputPassword) {

            setMessageType("success");
            setMessageText("Signed up successfully!");

        }else{

            setMessageType("error");
            setMessageText("Failed to sign up, check empty fields!");

        }

        console.log("Name: " + inputName + "\nEmail: " + inputEmail + "\nPassword: " + inputPassword);

        setInputName("");
        setInputEmail("");
        setInputPassword("");

        setTimeout(() => {
            setMessageType("");
            setMessageText("");
        }, 4000);

    }

    function checkForm(){

    }

    return(
        <div className={styles.contactform}>
            {<Message text={messageText} type={messageType}/>}
            <form onSubmit={event => event.preventDefault()}>
                <h1>Send us a message</h1>
                <p>Fill in all fields</p>
                    <input type="text" id="name" name="name" placeholder="Name"/><br/>
                    <input type="email" id="email" name="email" placeholder="Email"/><br/>
                    <input type="text" id="subject" name="subject" placeholder="Subject"/><br/>
                    <textarea id="message" name="message" placeholder="Message"></textarea><br/>
                <input type="submit" value="Send message" onClick={checkForm}/>
            </form>
        </div>
    );
}

export default ContactForm;
