function Message({ type, text }) {

    const [visible, setVisible] = useState(false);

    useEffect(() => {

        if(!text) {
            setVisible(false);
            return;
        }

        setVisible(true);

        setTimeout(() => {
            setVisible(false);
        }, 4000);

    }, [text]);

    return(
        <>
        </>
    );
}

export default Message;
